mapper.update:
UPDATE documents
emp_id = #{empId}, doc_type = #{docType}, doc_title = #{docTitle}, doc_content = #{docContent}, doc_retention_period = #{docRetentionPeriod}, doc_write_date = #{docWriteDate}, doc_report_date = #{docReportDate}, doc_complete_date = #{docCompleteDate}, doc_state = #{docState}, doc_read_yn = #{docReadYn}, doc_temp_yn = #{docTempYn}, doc_aprv_step = #{docAprvStep}, doc_max_step = #{docMaxStep}
WHERE doc_id = #{docId}

mapper.insert:
INSERT INTO documents (
    doc_id, emp_id, doc_type, doc_title, doc_content,
    doc_retention_period, doc_write_date, doc_report_date, doc_state,
    doc_read_yn, doc_temp_yn, doc_aprv_step, doc_max_step 
)
VALUES (
    #{docId}, #{empId}, #{docType}, #{docTitle}, #{docContent},
    #{docRetentionPeriod}, #{docWriteDate}, SYSDATE, #{docState},
    default, #{docTempYn}, default, #{docMaxStep}
)

mapper.select:
SELECT doc_id, emp_id, emp_name, doc_type, doc_title, doc_report_date, doc_complete_date, doc_state, doc_read_yn FROM ( SELECT rnum, doc_id, emp_id, emp_name, doc_type, doc_title, doc_report_date, doc_complete_date, doc_state, doc_read_yn FROM ( SELECT rownum as rnum, apl.doc_id, apl.emp_id, emp.emp_name, doc_type, doc_title, doc_report_date, doc_complete_date, doc_state, doc_read_yn FROM documents doc, approval_lines apl, employees emp
apl.emp_id = #{empId} AND doc.doc_id = apl.doc_id AND doc.doc_aprv_step = apl.aprv_line_order AND doc_temp_yn = 'N' AND emp.emp_id = doc.emp_id AND doc_state != '회수'
AND doc.doc_id = #{searchQuery.docId}
AND doc_title LIKE '%'||#{searchQuery.docTitle}||'%'
AND doc_state = #{searchQuery.docState}
AND emp.emp_name = #{searchQuery.empName}
AND doc_report_date BETWEEN TO_DATE((#{searchQuery.docReportStartDate}), 'YYYY-MM-DD HH24:MI:SS') AND TO_DATE((#{searchQuery.docReportEndDate}), 'YYYY-MM-DD HH24:MI:SS')
ORDER BY doc_report_date DESC ) WHERE rownum <= #{pager.rowsPerPage} * #{pager.pageNo} ) WHERE rnum > (#{pager.rowsPerPage} * (#{pager.pageNo} - 1))
